            Database database = Token.Instance.Database;
            DbCommand selectCommand = database.GetSqlStringCommand("SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SELECT COUNT(1) AS TotalRecords FROM ac_BasketItems WHERE WrapStyleId = @wrapStyleId");
            database.AddInParameter(selectCommand, "@wrapStyleId", System.Data.DbType.Int32, NullableData.DbNullify(wrapStyleId));
            return MakerShop.Utility.AlwaysConvert.ToInt(database.ExecuteScalar(selectCommand));
        }

