        /// <summary>
        /// Loads a collection of BasketItem objects for the given WrapStyleId from the database
        /// </summary>
        /// <param name="wrapStyleId">The given WrapStyleId</param>
        /// <param name="maximumRows">Maximum number of rows to retrieve.</param>
        /// <param name="startRowIndex">Starting index from where to start retrieving.</param>
        /// <returns>A collection of BasketItem objects</returns>
        [System.ComponentModel.DataObjectMethod(System.ComponentModel.DataObjectMethodType.Select)]
        public static BasketItemCollection LoadForWrapStyle(Int32 wrapStyleId, int maximumRows, int startRowIndex)
        {
            return LoadForWrapStyle(wrapStyleId, maximumRows, startRowIndex, string.Empty);
        }

        /// <summary>
        /// Loads a collection of BasketItem objects for the given WrapStyleId from the database
        /// </summary>
        /// <param name="wrapStyleId">The given WrapStyleId</param>
        /// <param name="maximumRows">Maximum number of rows to retrieve.</param>
        /// <param name="startRowIndex">Starting index from where to start retrieving.</param>
        /// <param name="sortExpression">The sort expression to use for sorting the loaded objects.</param>
        /// <returns>A collection of BasketItem objects</returns>
        [System.ComponentModel.DataObjectMethod(System.ComponentModel.DataObjectMethodType.Select)]
        public static BasketItemCollection LoadForWrapStyle(Int32 wrapStyleId, int maximumRows, int startRowIndex, string sortExpression)
        {
            //DEFAULT SORT EXPRESSION
            if (string.IsNullOrEmpty(sortExpression)) sortExpression = "OrderBy";
            //CREATE THE DYNAMIC SQL TO LOAD OBJECT
            StringBuilder selectQuery = new StringBuilder();
            selectQuery.Append("SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SELECT");
            if (maximumRows > 0) selectQuery.Append(" TOP " + (startRowIndex + maximumRows).ToString());
            selectQuery.Append(" " + BasketItem.GetColumnNames(string.Empty));
            selectQuery.Append(" FROM ac_BasketItems");
            selectQuery.Append(" WHERE WrapStyleId = @wrapStyleId");
            selectQuery.Append(" ORDER BY " + sortExpression);
            Database database = Token.Instance.Database;
            DbCommand selectCommand = database.GetSqlStringCommand(selectQuery.ToString());
            database.AddInParameter(selectCommand, "@wrapStyleId", System.Data.DbType.Int32, NullableData.DbNullify(wrapStyleId));
            //EXECUTE THE COMMAND
            BasketItemCollection results = new BasketItemCollection();
            int thisIndex = 0;
            int rowCount = 0;
            using (IDataReader dr = database.ExecuteReader(selectCommand))
            {
                while (dr.Read() && ((maximumRows < 1) || (rowCount < maximumRows)))
                {
                    if (thisIndex >= startRowIndex)
                    {
                        BasketItem basketItem = new BasketItem();
                        BasketItem.LoadDataReader(basketItem, dr);
                        results.Add(basketItem);
                        rowCount++;
                    }
                    thisIndex++;
                }
                dr.Close();
            }
            return results;
        }

        /// <summary>
        /// Gets the next value of the OrderBy field for BasketItem objects.
        /// </summary>
        /// <param name="basketId">The BasketId for which to get the next OrderBy value</param>
        /// <returns>The next value of the OrderBy field for BasketItem objects</returns>
        public static short GetNextOrderBy(Int32 basketId)
        {
            Database database = Token.Instance.Database;
            using (DbCommand selectCommand = database.GetSqlStringCommand("SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SELECT (Max(OrderBy) + 1) AS NextOrderBy FROM ac_BasketItems WHERE BasketId = @basketId"))
            {
                database.AddInParameter(selectCommand, "@basketId", System.Data.DbType.Int32, basketId);
                object result = database.ExecuteScalar(selectCommand);
                if (result.Equals(DBNull.Value)) return 1;
                return (short)(int)result;
            }
        }

